package org.centerServer.utils;

import java.math.BigDecimal;
import java.math.BigInteger;
import java.math.RoundingMode;
import java.security.SecureRandom;
import java.util.Random;

public class Paillier {
    // 密钥长度（比特）
    private static final int KEY_LENGTH = 1024;
    private static final int SCALE = 8; // 保留8位小数

    private static final BigInteger NEW_PAILLIER_N = new BigInteger(
            "");
    private static final BigInteger NEW_PAILLIER_LAMBDA = new BigInteger(
            "");
    private static final BigInteger NEW_PAILLIER_G = new BigInteger(
            "");
    private static final BigInteger NEW_PAILLIER_MU = new BigInteger(
            "");
    private static final int NEW_PAILLIER_SCALE = 8;

    public static final Paillier NEW_PAILLIER = new Paillier(NEW_PAILLIER_N, NEW_PAILLIER_G, NEW_PAILLIER_LAMBDA,
            NEW_PAILLIER_MU, NEW_PAILLIER_SCALE);

    // 公钥参数
    private static final BigInteger n = new BigInteger(
            "");
    private static final BigInteger g = n.add(BigInteger.ONE);

    // 私钥参数
    private static final BigInteger lambda = new BigInteger(
            "");

    // 计算mu = (L(g^lambda mod n^2))^(-1) mod n
    private static final BigInteger mu = new BigInteger(
            "");

    // 新增：实例变量和构造函数
    private final BigInteger nInst;
    private final BigInteger gInst;
    private final BigInteger lambdaInst;
    private final BigInteger muInst;
    private final int scaleInst;

    public Paillier(BigInteger n, BigInteger g, BigInteger lambda, BigInteger mu, int scale) {
        this.nInst = n;
        this.gInst = g;
        this.lambdaInst = lambda;
        this.muInst = mu;
        this.scaleInst = scale;
    }

    // 新增：实例加密方法
    public BigInteger encryptInst(BigDecimal m) {
        BigDecimal scaled = m.setScale(scaleInst, RoundingMode.HALF_UP);
        BigInteger m_int = scaled.multiply(BigDecimal.TEN.pow(scaleInst)).toBigInteger();
        if (m_int.compareTo(BigInteger.ZERO) < 0) {
            m_int = m_int.mod(nInst);
        }
        Random rng = new SecureRandom();
        BigInteger r = new BigInteger(KEY_LENGTH, rng);
        BigInteger n2 = nInst.multiply(nInst);
        BigInteger gm = gInst.modPow(m_int, n2);
        BigInteger rn = r.modPow(nInst, n2);
        return gm.multiply(rn).mod(n2);
    }

    // 新增：实例解密方法
    public BigDecimal decryptInst(BigInteger c) {
        BigInteger n2 = nInst.multiply(nInst);
        BigInteger c_lambda = c.modPow(lambdaInst, n2);
        BigInteger L = c_lambda.subtract(BigInteger.ONE).divide(nInst);
        BigInteger m = L.multiply(muInst).mod(nInst);
        BigInteger halfN = nInst.divide(BigInteger.TWO);
        if (m.compareTo(halfN) > 0) {
            m = m.subtract(nInst);
        }
        return new BigDecimal(m).divide(BigDecimal.TEN.pow(scaleInst), scaleInst, RoundingMode.HALF_UP);
    }

    public static BigInteger encrypt(BigDecimal m) {
        // 将小数转换为整数（乘以10^8保留8位小数）
        BigDecimal scaled = m.setScale(SCALE, RoundingMode.HALF_UP);
        BigInteger m_int = scaled.multiply(BigDecimal.TEN.pow(SCALE)).toBigInteger();

        // 只对负数使用模n对称表示
        if (m_int.compareTo(BigInteger.ZERO) < 0) {
            m_int = m_int.mod(n);
        }

        // 生成随机数r
        Random rng = new SecureRandom();
        BigInteger r = new BigInteger(KEY_LENGTH, rng);

        // 计算密文 c = g^m * r^n mod n^2
        BigInteger n2 = n.multiply(n);
        BigInteger gm = g.modPow(m_int, n2);
        BigInteger rn = r.modPow(n, n2);
        return gm.multiply(rn).mod(n2);
    }

    public static BigDecimal decrypt(BigInteger c) {
        BigInteger n2 = n.multiply(n);
        BigInteger c_lambda = c.modPow(lambda, n2);
        BigInteger L = c_lambda.subtract(BigInteger.ONE).divide(n);
        BigInteger m = L.multiply(mu).mod(n);

        // 处理负数情况
        BigInteger halfN = n.divide(BigInteger.TWO);
        if (m.compareTo(halfN) > 0) {
            m = m.subtract(n);
        }

        // 将整数转换回小数（除以10^8）
        return new BigDecimal(m).divide(BigDecimal.TEN.pow(SCALE), SCALE, RoundingMode.HALF_UP);
    }

    // 获取公钥参数
    public static BigInteger getPublicKey() {
        return n;
    }

    // 获取生成元
    public static BigInteger getGenerator() {
        return g;
    }

    // 获取模数n的平方
    public static BigInteger getN2() {
        return n.multiply(n);
    }
}